package org.opentutorials.javatutorials.numberstring;

public class Variable {

	public static void main(StringEx[] args) {
		// TODO Auto-generated method stub
		
			//	변수 (Variable) = 변할수 있는 값 (모든데이터 = 숫자,문자 -> 숫자에만 한정X)
			// 그,그것,그녀 처럼 대명사로서 역할을 하는 값
		
		//데이터의 형식 = int => Interger의 줄임말
		//변수의 이름 	= a
		
		int a; //a라는 변수 안에 담길수 있는 값은 숫자다. 라고 "변수를 선언하는것" (int)
		a = 1; //a라는 변수값에 1이라는 값을 대입한것
		System.out.println(a+1);	// (a=1), +1 = 2
		
		a = 2; // a라는 값을 1에서 2로 재할당
		System.out.println(a+1); // a는 2로 재할당이 되어 2+1 = 3으로 출력
		
		
		
		
		
		
	}

}
